float rand(vec2 co)
{
    return fract(sin(dot(co.xy, vec2(156782.94567898, 78467897675.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 归一化像素坐标 (从0到1)
    vec2 uv = fragCoord / iResolution.xy;

    // 基于片元坐标生成随机噪音
    float noise = rand(uv * iTime);

    // 使用噪音扰动颜色
    vec3 col = 0.5 + 0.5 * cos(cos(iTime) + uv.xyx + vec3(0, 2, 4)) + noise * 0.1;

    // 将颜色输出到屏幕
    fragColor = vec4(col, 1.0);
}
