float sdf(vec2 c, float r, vec2 pos)
{
     if (r >  distance(c, pos) )
     {
         return 1.0;
     }
     return 0.0; //0.5 * cos(iTime);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = 2.0* ((fragCoord/iResolution.xy) - 0.5);

    uv.x *= (iResolution.x/iResolution.y);  


    uv = fract(uv);
    uv = abs( round(uv) - uv );

     float clr = sdf(vec2(0.0, 0.0), 0.5 * sin(iTime), vec2(uv.x, uv.y) );
    fragColor = vec4(clr, uv.y * cos(iTime)/uv.x,uv.x/ uv.y, 1.0);
}
