void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Define corner colors
    vec3 topLeft = vec3(0.0, 0.0, 1.0);    // Max blue
    vec3 topRight = vec3(1.0, 0.0, 0.0);   // Max red
    vec3 bottomLeft = vec3(1.0, 1.0, 1.0); // White
    vec3 bottomRight = vec3(0.0, 1.0, 0.0); // Max green

    // First stage: Interpolate top and bottom colors
    vec3 topColor = mix(topLeft, topRight, uv.x);
    vec3 bottomColor = mix(bottomLeft, bottomRight, uv.x);

    // Second stage: Interpolate between top and bottom
    vec3 finalColor = mix(bottomColor, topColor, uv.y);

    // Output to screen
    fragColor = vec4(finalColor, 1.0);
}
