void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Set up some variables
    vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime * 0.7;
    float zoom = 32.;

    // Calculate the position of the point on the fractal based on the zoom level
    vec2 pos = 0.02*sin(iTime*uv) - (uv.x * (0.8*sin(time) / 5.) * vec2(zoom, 0.));

    // Set up some variables for the color scheme
    float hue = abs(pos.x * 10.) + abs(pos.y * 3.);
    vec3 col;
    col.r = fract(hue / 6.);
    col.g = fract((hue / 4.) + 0.2);
    col.b = fract((hue / 5.) + 0.3);

    // Set the color of the pixel based on the position and hue
    fragColor = vec4(col, 1.);
}
