// Fork of "Log(Z)^2 with lighting" by wyatt. https://shadertoy.com/view/X3jSWc
// 2024-03-23 01:54:41

Main {

    Q = sin(A(U)/float(1+iFrame));
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
 #define UIF (1.0 / float(0xffffffffU))
vec4 hash44(uvec4 q)
{
	q *= uvec4(1597334673U, 3812015801U, 2798796415U, 1979697957U);
	q = (q.x ^ q.y ^ q.z ^ q.w)*uvec4(1597334673U, 3812015801U, 2798796415U, 1979697957U);
	return vec4(q) * UIF;
}
#define pi 3.141592659

vec2 mul (vec2 a, vec2 b) {
	return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);
}
vec2 f (vec2 v) {
    for (int i = 0; i < 101; i++) {
        v = mul(v,v)-vec2(.8,.1);
        if (length(v)>4.) return vec2(1);
    }
    return vec2(0);//vec2(.25,.05)*max(v,0.);
}
Main 
{	
    Q = vec4(0);
    for (float x = 0.; x<2.;x++)
    for (float y = 0.; y<2.;y++)
    {
        vec2 uv = 2.*(U+.5*vec2(x,y)-.5*R)/R.y;
        float t = 0.1*iTime;
        float scale = 1.;
        vec2 v = scale*uv;

        Q += vec4(f(v),0,1.0);
    }
}

Main {

    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,0));
    vec4 w = A(U-vec2(0,0));
    Q.xy = A(U).xy;
    Q.z = length(e.xy)-length(w.xy);
    Q.w = length(n.xy)-length(s.xy);

}

Main {
    Q = A(U);
    vec4 h = hash44(uvec4(U,iFrame,iTime));

    vec2 d = vec2(cos(h.x*2.*pi),sin(h.x*2.*pi));


    for (float i = 0.; i < 100.; i++) {

        U += 2.5*d;
        vec4 b = B(U);
        d = normalize(d-(.1+.5*h.w)*b.zw);
        vec4 q = 1.5*(length(b.xy*vec2(.7,1)))*max(sin(6.*h.w-2.+vec4(1,2,3,4)),0.);
        Q += .5*q/100.;

    }
    if (iFrame < 1) Q = vec4(0);
}
