#define I iResolution
#define LIANGDIANSHI( u, a, b ) (u.x-a.x)/(b.x-a.x) - (u.y-a.y)/(b.y-a.y) 

float drawLine( vec2 u, vec2 a, vec2 b ){
    float p = LIANGDIANSHI( u, a, b );

    p = smoothstep( 5. / I.y, 0., abs(p) ) ;

    p += step( length( u - (a+b)/2. ) - length(a-b)*.5, 0. );

    return p;
}

// sd round from IQ
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    p -= a; b -= a;
	return length( p-b*clamp(dot(p,b)/dot(b,b),0.0,1.0) );
}

void mainImage( out vec4 U, in vec2 V )
{
    U.xy = ( V * 2. - I.xy ) / I.y,
    U.wz = ( iMouse.xy * 2. - I.xy ) / I.y;

    vec3 c;
    float p = smoothstep( 5. / I.y, 0., abs(drawLine( U.xy,  U.wz, vec2(0.55, 0.08) )) ) * .2;

    p += smoothstep( 5. / I.y, 0., abs(sdSegment( U.xy,  U.wz, vec2(0.55, 0.08) )) ) * .5;


    U = vec4( p );
}
