/**
 * Draw a circle at vec2 `pos` with radius `rad` and
 * color `color`.
 */
vec4 drawCircle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

  float radius=.5;
  float borderWidth=.2;
  float smoothness=.05;

  vec3 circleColor=vec3(1.);
  vec3 borderColor=vec3(0.);

  float dist=length(uv-vec2(.5,.5));//+ (1.-mod(iTime*0.05,1.)+0.2);

  float border=smoothstep(radius-borderWidth,radius+borderWidth,dist);
  float circle=smoothstep(radius-borderWidth-smoothness,radius-borderWidth,dist);

  vec3 color=mix(borderColor,circleColor,circle);

    // Output to screen
    fragColor = vec4(color, border);
}
