float h21(vec2 p){
    p.x *= sin(221.293 * p.y /2.) + 21.393;
    p.y *= dot(p.yx, vec2(1293.12, 9318.192));
    vec2 t = vec2((p.x * 213.023)*.431, fract(sin(p.y)*921.21));
    return fract(dot(t, p));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvOg1 = uv;
    vec2 uvOg = uv;
    uvOg -=.5;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;
    vec3 col = vec3(0.);
    uv.x += iTime*.001;
    vec4 finCol = texture(iChannel0, uvOg1);
    float vig = 1. - length(uvOg);
    finCol.rgb *= h21(uv*5.)+.5 ;
    //finCol=vec4(0.);
    finCol.rgb *= vig;

    finCol.rgb = finCol.rrr;

    fragColor = finCol;
}
