#define cor(a) (cos(a * 6.3 + vec3(0, 23, 21)) * .5 + .5)
#define wmod(p, w) mod(p, w) - w/2.
#define PI 3.14
#define t iTime * .5

// https://www.shadertoy.com/view/cd3yRB

void mainImage(out vec4 o, vec2 u) {    
    vec2  r = iResolution.xy;
          u = (u - r.xy / 2.) / r.y;
          o *= 0.;

    vec3 p;      
    float i, a, d, z;

    while(i++ < 99.) {
        p = a * vec3(u, .5);

        z = p.z + t * 5.;

        p.z = wmod(z, 2.), 
        p.x = 
          cos(
              wmod(
                  atan(p.y, p.x) + tanh((cos(i))*18.) * .4 + z * .9, 
                  PI / 1.5 // 1.5 ... 3 
              )  
          ) 
          * length(p.xy) - 4., 

        a += d = max(abs(length(p.xz) - .2), .01);

        o.rgb += exp(-d * 5.) * cor(z * .3) * .1;
    }
}
