float smin( float a, float b, float k )
{
    k *= 1.0;
    float r = exp2(-a/k) + exp2(-b/k);
    return abs(k*log2(r));
}

float box(vec2 p) {
    vec2 q = abs(p);
    return max(q.x,q.y);
}

float ai(vec2 p){
    float smoothing = .075;

    float top = box(p*vec2(2.5,12)-vec2(0,4));

    float s_t = box((p*vec2(12,4)-vec2(-1.5,.83))*mat2(vec2(1,-.3),vec2(0,1)));
    float s_m = box(p*vec2(3,12)-vec2(0,1.5));
    float s_b = box(p*vec2(12,1.75)-vec2(1.5,-.3));
    float s = smin(s_t, smin(s_m, s_b, smoothing), smoothing);

    float curve = -pow(abs(min(0.2,p.y)-0.2),2.0)*1.6;
    float pie = box((p*vec2(12,1.25)-vec2(curve,-.07)));

    return smoothstep(0.0,8./iResolution.y,
    smin(top,smin(s,pie,smoothing),smoothing)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv-.5)*vec2(iResolution.x/iResolution.y, 1.0);

    mat3 remap = mat3(vec3(
    .4,.2,.4
    ),vec3(
    .1,.1,0.8
    ),vec3(
    0,0.8,.2
    ));
    vec3 col = sin(vec3(uv,1.6)*remap*vec3(16.,24.,6.)+vec3(-1.35,.7,.36))*.5+.5;

    fragColor = vec4(mix(col, vec3(1), ai(uv)),1.0);
}
