const int AMOUNT = 13;

void mainImage(out vec4 fragColor, vec2 fc)
{
    vec2 coord = 10.0 * (fc.xy - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    float len;
    float a = abs(sin(pow(iTime,0.001)));
    float a2 = abs(sin(pow(iTime,0.001)));

    for(int i = 0; i < AMOUNT; i++){
        coord *= mat2(sin(a), -sin(a), sin(a), cos(a));
        coord = abs(coord);
        len = length(vec2(coord.x, coord.y))*sin(80.0) / cos(3.14159265358);

        coord.x = coord.x - cos(coord.y + sin(len)) + cos(iTime / 9.0);
        coord *= mat2(sin(a2), -sin(a2), sin(a2), cos(a2));
        coord.y = coord.y + sin(coord.x + cos(len)) + sin(iTime / 9.0);

    }

    fragColor = vec4(cos(len * 1.1), cos(len * 1.05), cos(len * 1.01), 1.0);
}
