void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    uv.y = tan(uv.y);

    // Rotate UV coordinates by 90 degrees
    uv = vec2(uv.y, -uv.x);

    float zoom = 1.0 + 0.5 * sin(iTime * 0.1);
    uv /= zoom;

    float angle = iTime * 0.1;
    float s = sin(angle);
    float c = cos(angle);
    uv = mat2(c, -s, s, c) * uv;

    vec3 col = vec3(1.5);
    float grid = step(0.9, fract(uv.x * 10.0) * fract(uv.y * 10.0) - 0.1);
    col = mix(vec3(0.2, 0.5, 0.0), vec3(1.0, 0.5, 0.2), grid);

    float glitch = float(int(abs(uv.y) * 10.0) ^ int(abs(uv.x) * 2.0) & int(iTime * 10.0)) * 0.1;
    col *= vec3(glitch, glitch * 0.5, glitch * 0.2);

    col = col * 2.5 - 0.5;
    col = abs(col);

    float lines = step(0.22, abs(sin(uv.x * 20.0 + iTime) * 0.5)) * 0.2 +
                  step(2.42, abs(sin(uv.y * 10.0 + iTime) * 0.5)) * 0.2;
    col += vec3(lines);

    vec3 gradient = vec3(uv.x, uv.y, 1.0 - abs(uv.x + uv.y)) * 0.2 + 0.5;
    col = mix(col, gradient, 0.5);

    float noise = fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
    col += noise * 0.1;

    col = clamp(col, 0.0, 1.0);
    col = 1.0 - col;

    fragColor = vec4(col, 1.0);
}
