vec2 R; vec4 M; float T; int I;
#define Main void mainImage(out vec4 Q, vec2 U){UNIS
#define UNIS R=iResolution.xy;M=iMouse;T=iTime;I=iFrame;
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)

#define G(x) 0.3989422804/std*exp(-x*x/std/std)

float building(vec2 U) {
    return step(length(U-.5*R),100.*R.y);//+step(U.y,.1*R.y);
}
bool cell (vec2 u) {
    return u.x>=0.&&u.y>=0.&&u.x<1.&&u.y<1.;
}
float _12(vec2 U) {
    U = floor(U);
    return U.x+U.y*R.x;
}
vec2 _21(float i) {
    return .5+vec2(mod(i,R.x),floor(i/R.x));
}
float G2 (float w, float s) {
    return 0.15915494309*exp(-.5*w*w/s/s)/(s*s);
}
#define normaliz(v) (length(v)>0.?normalize(v):vec2(0))

Main
    float bu = building(U);
    if (bu==0.) {Q = vec4(0); return;}

    Q = A(U);
    vec4 d = D(Q.xy-.5);
    float s = float(U.x<.5*R.x)*2.-1.;
    vec2 f = vec2(1./R.y*s,0);
    for (int x=-3;x<=3;x++)
    for (int y=-3;y<=3;y++) {
       vec2 u = vec2(x,y);
       vec4 b = B(Q.xy+u-.5);
       for (int k = 0; k < 4; k++) if (b[k]>0.) {
           vec2 u = _21(b[k]);
           vec4 a = A(u);
           vec2 r = a.xy-Q.xy;
           float l = length(r);
           if (l>0.)
           f += (r/l*(
               -0.3*exp(-.6*l)

               )+.5*(a.zw-Q.zw)*pow(max(dot(normaliz(a.zw-Q.zw),r/l),0.),4.)
               ) 
               *G2(l,5.);
       }
       vec4 c = D(Q.xy+u-.5);
       if (length(u)>0.) f += (c.x*(-1.-c.y*s-c.x))*normalize(u)*G2(length(u),3.);
    }
    Q.zw += f;
    Q.xy += Q.zw;

    if (Q.y<5.) Q.w += .1;
    if (Q.x<5.) Q.z += .1;
    if (R.y-Q.y<5.) Q.w -= .1;
    if (R.x-Q.x<5.) Q.z -= .1;

    if (M.z>0.) Q.zw -= 1e-2*(M.xy-Q.xy)/(1.+length((M.xy-Q.xy)));

    if(I<1) {

        Q = vec4(U-.5,0,0);
        // if (length(U-vec2(.9)*R)<.02*R.x) Q.zw = vec2(-2.5,-1.5);
    }

}

Main
