         vec4 ave = vec4(0);
         for (int k = 0; k < 4; k++) if (b[k]>0.) {
            vec2 u = _21(b[k]);
            vec4 a = A(u);
            ave.x += 1.;
            if (u.x<.5*R.x)
                ave.y -= 1.;
            else  ave.y += 1.;
        }
        Q += G(x)*ave;
    }
}

