float circleSDF(vec2 p, float r)
{
    return length(p) - r;
}

vec2 normalizeCoord(in vec2 p)
{
    return (2.0f * p - iResolution.xy) / iResolution.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 orange = vec3(250.0f, 208.0f, 0.0f) / 255.0f;
    vec3 red = vec3(1.0f, 0.0f, 0.0f);

    vec2 center = vec2(0.0f);

    vec2 uv = normalizeCoord(fragCoord);

    vec3 color;
    float d = circleSDF(uv - center, 0.5);

    color = d > 0.0 ? red : orange;

    color *= (1.0f - exp(-12.0 * abs(d)));

    color = 0.8 * color + 0.2 * sin(150.0f * d + 4.0f + 40. * iTime) * color;

    //mouse click position

    vec2 m = normalizeCoord(iMouse.xy);

    float CD = abs(circleSDF(m, 0.5));
    if (iMouse.z > .1)
    {
        color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(.01, 0.0, abs(length(m - uv) - CD)));

       color = mix(color, vec3(1.0, 1.0f, 0.0), smoothstep(0.02, 0.01, abs(length(m - uv))));

    }

    fragColor = vec4(color, 1.0f);
}
