#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
float happy_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    vec2 uv = C.xy / iResolution.xy-0.5;
     float time = iTime * 0.0;
    float angle = atan(uv.y, uv.x) + time;
    float radius = length(uv);

    // Complex pattern blending
    float wave1 = sin(radius * 10.0 - iTime * 2.0);
    float wave2 = cos(angle * 6.0 + iTime * 1.5);
    float pattern = wave1 * wave2;

    // Color modulation with gradient and patterns
    vec3 baseColor = vec3(0.2, 0.3, 0.6);
    vec3 gradient = 0.5 + 0.5 * vec3(sin(angle * 2.0), cos(angle * 2.0), sin(radius * 5.0));
    vec3 color = baseColor + 0.4 * gradient * pattern;

    // Add dynamic light streaks
    float streaks = abs(sin(angle * 8.0 - iTime * 2.0)) * exp(-radius * 10.0);
    color += vec3(streaks, streaks * 0.8, streaks * 0.6);

    // Subtle noise
    float noise = fract(sin(dot(uv * 80.0, vec2(12.9898, 78.233))) * 43758.5453);
    color += 0.05 * vec3(noise);
    for(
        float i=0.,g=0.,e,s;
        ++i<99.;
        O.rgb+=mix(r/r,H(log(s)),.7)*.08*exp(-i*i*e))
    {
        p=g*d+color;
        p.z-=.6;
        p=R(p,normalize(vec3(1,2,3)),iTime*.0);
        s=4.;
        for(int j=0;j++<8;)
            p=abs(p),p=p.x<p.y?p.zxy:p.zyx,
            s*=e=1.8/min(dot(p,p),1.3),
            p=p*e-vec3(12,3,3);
        g+=e=length(p.xz)/s;

    }
    O=pow(O,vec4(5));
     float anim = sin(iTime * 12.0) * 0.1 + 1.0;  // anim between 0.9 - 1.1
    O += vec4(happy_star(uv, anim) * vec3(0.35,0.2,1.15)*0.1, 1.0);
 }
