void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalizamos las coordenadas
    vec2 uv = fragCoord/iResolution.xy;

    // Generamos un parpadeo r치pido (r치pido strobo)
    float strobe = mod(iTime * 30.0, 2.0); // Aumento la frecuencia para hacerlo m치s r치pido
    strobe = step(1.0, strobe); // Convierte el valor en 0 o 1

    // El fondo es negro
    vec3 color = vec3(0.0, 0.0, 0.0);

    // Si strobe es 1, mostramos el color amarillo
    if (strobe > 0.5) {
        color = vec3(1.0, 1.0, 0.0); // Amarillo
    }

    // Asignamos el color final al fragmento
    fragColor = vec4(color, 1.0);
}
