void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * vec2(0.2, 1.0);

    vec3 col0 = texture(iChannel0, uv).xyz;
    col0 = (floor(col0 * 3.0) + 0.5) / 3.0;
    col0 *= vec3(1.0, 0.9, 0.8);

    vec2 coord = uv * (iChannelResolution[0].xy) - 0.5;
    ivec2 limit = ivec2(iChannelResolution[0].xy) - 1;
    ivec2 icoord = ivec2(coord);
    vec2 fac = coord - vec2(icoord);
    ivec2 e = ivec2(0, 1);
    vec3 col1 = mix(
        mix(texelFetch(iChannel0, clamp(icoord + e.xx, ivec2(0), limit), 0).xyz, texelFetch(iChannel0, clamp(icoord + e.yx, ivec2(0), limit), 0).xyz, fac.x),
        mix(texelFetch(iChannel0, clamp(icoord + e.xy, ivec2(0), limit), 0).xyz, texelFetch(iChannel0, clamp(icoord + e.yy, ivec2(0), limit), 0).xyz, fac.x),
        fac.y
    );
    col1 = (floor(col1 * 3.0) + 0.5) / 3.0;
    col1 *= vec3(0.8, 0.9, 1.0);

    vec3 col = iMouse.x < fragCoord.x ? col0 : col1;

    fragColor = vec4(col, 1.0);
}
