void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 sectorSize = vec2(0.25, 0.25); // размер сектора

    // Определяем индекс сектора для каждого пикселя
    vec2 sectorIndex = floor(uv / sectorSize);
    vec2 sectorUV = fract(uv / sectorSize);

    // Переставляем пиксели между секторами
    vec2 newSectorIndex = vec2(sectorIndex.y, sectorIndex.x);
    vec2 newUV = (newSectorIndex + sectorUV) * sectorSize;

    fragColor = texture(iChannel0, newUV);
}
