void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize pixel coordinates to [0, 1]
    vec2 uv = fragCoord / iResolution.xy;

    // Center the coordinates
    uv -= 0.5;

    // Create a rotating kaleidoscope effect
    float angle = atan(uv.y, uv.x);
    float segments = 6.0; // Number of segments (adjust as desired)
    angle = mod(angle + iTime * 0.5, 2.0 * 3.14159 / segments);
    angle = abs(angle - 3.14159 / segments);

    // Neon colors
    vec3 neon1 = vec3(1.0, 0.0, 0.5); // Magenta
    vec3 neon2 = vec3(0.0, 1.0, 0.2); // Green
    vec3 neon3 = vec3(0.2, 0.5, 1.0); // Blue

    // Combine neon colors based on angle
    vec3 finalColor = mix(neon1, mix(neon2, neon3, angle), 0.8);

    fragColor = vec4(finalColor, 1.0);
}
