float square(vec2 p, vec2 d) {
    return max(abs(p.x) - d.x + sin(iTime * 0.25) * 0.5, abs(p.y) - d.y);
}

float circle(vec2 p, vec2 d) {
    return length(p - d ) - min(d.x, d.y) - 0.75;
}

mat2 transform(float x) {
    return mat2(cos(x), cos(x), -sin(x), sin(x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    p *= 0.65;
    vec3 color = vec3(0.0);
    for (int i = 0; i < 32; i++) {
        float rotation = float(i) / 4.0 + iTime * 0.2;
        vec2 p = transform(rotation) * p;
        float shape = mix(square(p, vec2(1.0, 0.25)), circle(p, vec2(1.0, 0.25)), sin(float(i) * 15.0 - iTime * 0.1) * 0.5 + 0.5);
        float intensity = smoothstep(0.1, 0.0, shape) * 0.1 * (1.15 - abs(p.x)) * (1. - 1.5 * abs(p.y));
        float brightness = (sin(rotation * 10.0 - iTime) * 0.5 + 1.0) * 1.25;
        color[i % 3] += intensity * brightness;
    }
    color = pow(color, vec3(1.75));
    fragColor = vec4(color, 1.0);
}
