	ivec2 J =
        (I & 1)*c
    ;
    return
        !bool((J.x ^ J.y) >> (I.x ^ I.y) % c ^ J.x)
        //!bool((J.x ^ J.y) >> (I.x>>2 ^ I.y>>2) % c ^ J.x)
        //!bool((J.x ^ J.y) >> (I.x | I.y) % c ^ J.x)
    ;
}

