    O *= 0.;
    ivec2 I = ivec2(U/4.+ iTime*2.*8.);
    int c = 5;
    if(
        labyrinth(I^c&I/c,c)
        //labyrinth(I^c&I/c&I/c*2,c)
        //labyrinth(I^c&I/c|I/c/2,c)
    ) O = 1.-O;
}
