float parabola(float x, float height)
{
    return 6.0 * height * x * (1.0 - x);
}

mat2 rotate2d(float a){
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.y;
    float time = mod(iTime, 12.0);
    vec4 background = texture(iChannel0, uv * rotate2d(-iTime / 8.0));

    float dayPhase = mod(time - 3.0, 6.0) / 6.0;
    vec2 sunPos = vec2(1.0 - dayPhase, 0.1 + parabola(dayPhase, 0.4));
    sunPos.x *= iResolution.x / iResolution.y;
    float sunRadius = 0.08;
    float sunDist = distance(uv, sunPos);
    vec3 sunColor = vec3(1.0, 0.8, 0.4);

    float nightPhase = mod(time + 3.0, 12.0) / 6.0;
    vec2 moonPos = vec2(1.0 - nightPhase, 0.1 + parabola(nightPhase, 0.4));
    moonPos.x *= iResolution.x / iResolution.y;
    float moonRadius = 0.08;
    float moonDist = distance(uv, moonPos);
    vec3 moonColor = vec3(0.9, 0.9, 1.0);

    float t = abs(time - 6.0) / 6.0;
    vec3 skyColor1 = vec3(0.4, 0.6, 0.8);
    vec3 skyColor2 = vec3(1.0, 0.6, 0.0);
    vec3 skyColor3 = vec3(0.1, 0.3, 0.6);
    vec3 skyColor4 = vec3(0.1, 0.2, 0.5);
    vec3 skyColor = mix(mix(skyColor1, skyColor2, smoothstep(0.0, 0.6, t)), mix(skyColor3, skyColor4, smoothstep(0.6, 1.0, t)), smoothstep(0.0, 1.0, t));

    vec3 waterColor1 = vec3(0.2, 0.5, 0.7);
    vec3 waterColor2 = vec3(0.9, 0.4, 0.0);
    vec3 waterColor3 = vec3(0.0, 0.2, 0.4);
    vec3 waterColor4 = vec3(0.0, 0.1, 0.3);
    vec3 waterColor = mix(mix(waterColor1, waterColor2, smoothstep(0.0, 0.6, t)), mix(waterColor3, waterColor4, smoothstep(0.6, 1.0, t)), smoothstep(0.0, 1.0, t));

    vec3 col = mix(waterColor, skyColor, uv.y);
    col = mix(col, background.xyz, t * t / 3.0);
    col = mix(col, sunColor, smoothstep(sunRadius, sunRadius - 0.01, sunDist));
    col = mix(col, moonColor, smoothstep(moonRadius, moonRadius - 0.01, moonDist));
    fragColor = vec4(col, 1.0);
}
