int detect( in ivec2 p )
{
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    return (texelFetch(iChannel0, p, 0 ).x > 0.5 ) ? 1 : 0;
}
float random( float n )
{
    return fract(sin(n)*3141.5165358*iDate.w);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    ivec2 x = ivec2(fragCoord);
	int around =  detect(x+ivec2(-1,-1)) + detect(x+ivec2(0,-1)) + detect(x+ivec2(1,-1)) + detect(x+ivec2(-1, 0)) + detect(x+ivec2(1, 0))+ detect(x+ivec2(-1, 1)) + detect(x+ivec2(0, 1)) + detect(x+ivec2(1, 1));
    int self = detect(x);
    float f;
    if(iFrame==0) {
    f = step(0.5, random(fragCoord.x*11.4+random(fragCoord.y*51.4)));
    }else{
    f = (((around==2)&&(self==1))||(around==3)) ? 1.0 : 0.0;
    }
	fragColor = vec4(f,0.0, 0.0, 0.0);
}
