#define rot(r) mat2(cos(r+vec4(0,33,11,0)))

void mainImage(out vec4 o, vec2 u) {    
    float c, d, i, j, g, e = 1., t = iTime * .5;

    for(o *= 0.; i++ < 20. ; g += e, o += 9e-4/e) {
        vec3 r = iResolution,   
             p = vec3( (u - r.xy / 2.) / r.y * g , g - 5. - i/11e2 );

        p.y += cos(t * 3.) + 1.6;
        p.x += sin(t * 2.);

        for(j = 0.; j++ < 6.; p *= 1.3 ) 
            d = length(max( abs(p) - vec3(.1,.6,.3), 0.)) * pow(.76, j),
            d > e ? c
                : e = d, c = j,

            p.xz *= rot(cos(t * j * .3) * 2.),
            p.x = abs(.8 * p.x - cos(2. * t)) - .6,
            p.y -= 1.,
            p.xy *= rot(sin(t * 2.1) * .5 + 1.2 );

    }
    o.xy *= c / 48.;
}
