void mainImage(out vec4 O, vec2 U)
{
        vec2 R = iResolution.xy,
             u = (U+U-R)/R.y*45.,
             k = vec2(.3,.1);
        float d;
        for(int i=0;i<13;i++)
        {
                u -= 2.5 * sin(k.yx*u.yx + cos(k*u.yx))  + .3*sin(iTime*6.28+u*.1) ;// *.1*u.yx*mat2( cos(u.yx),exp(abs(cos(d*.1))),i/2);
                d += length(u)*.5;
        }
      //O = .6+.6*cos(6.28*(  floor(d *1.7 +.2) +.65+vec4(0,.1,.2,0)));
        O = .5+.6*cos(6.28*(  floor(d *1.7 +.2) +.65+vec4(.2,.1,0,0)));
}
