float random (in float x) {
    return fract(sin(x)*1e4);
}

float random (in vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

float pattern(vec2 st, vec2 v, float t) {
    vec2 p = floor(st+v);
    return step(t, random(100.+p*.000001)+random(p.x)*0.5 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    // Normalized mouse coordinates (from 0 to 1)
    vec4 m = vec4(iMouse.xy / iResolution.xy, iMouse.zw / iResolution.xy);

    float s = .5 + .5 * sin(iTime);


    vec2 grid = vec2(50.,50.);
    uv *= grid;

    vec2 ipos = floor(uv);//int
    vec2 fpos = fract(uv);//frac

    vec2 vel = vec2(iTime * 1. * max(grid.x,grid.y)); //time
    vel *= vec2(-.5,0)* random(1. + ipos.y);//direction

    vec2 offset = vec2(2.*(1. - m.x)*(.8 + 5.*sin(random(iTime))),.0);

    vec3 color = vec3(0.);
    color.r = pattern(uv + offset, vel + random(iTime*2.), .5+m.x);
    color.g = pattern(uv, vel+ random(iTime*2.), .5+m.x);
    color.b = pattern(uv - offset, vel+ random(iTime*2.), .5 + m.x);

    color *= step(.5, fpos.y);






    // Output to screen
    fragColor = vec4(color,1.0);
}
