#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime
#define fogo vec3(8, 3.25, 1).xyz * 6.
#define carne vec3(7, 5, 4) / 1.

void mainImage(out vec4 o, vec2 u) {    
    vec2 r = iResolution.xy;

    float i, dd1, dd2, dd, d = 1., a;
    vec3 p, q, D, cor;

    // camera
    p = vec3(0, 0, cos(t * .5) * 2.1);
    D = vec3((u - r.xy / 2.) / r.y, 1.4);
    D.zy *= rot(cos(t * 3.) * .05);
    D.zx *= rot(sin(t * .2) * .5);

    while (i++ < 60. && d > 1e-3) {

        cor = carne;
        a = 1.; d = -.49;
        while(a < 32.)
            q = p,
            q.xy *= rot(a),
            q.yz *= rot(a),
            q = fract(q * a) - .5,
            d += length(q) * .6 / a,
            a += a;

        if(abs(p.y*3.5) < abs(q.x*3.5) ){
            //gordura
            cor.yz = cor.yz * .05;
            d = d + .02;
        }

        // fogo
        q = p - sin(
                    vec3(3., 2., 4.) * t + vec3(0, 11, 0))
                        * vec3(.3, .4, 1) - vec3(0, 0, 2);

        dd1 = dot(abs(q), vec3(.4)) - .1;
        q.xy *= rot(t * 4.);
        q.zy *= rot(t * 5.);
        dd2 = dot(abs(q), vec3(.4)) - .11;
        q.xy *= rot(t * 2.);
        q.zy *= rot(t * 3.);
        dd = min(dd1, dd2);

        1.4 * dd < d
            ? cor = fogo, d = .08 * dd
            : d;


        p += D * d * .4; 
    }

    o.rgb = 1. - exp(-2.5 * cor / i + i / 61. - .5);
}
