void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 1.5 + 1.5 * cos(iTime+uv.xyx+vec3(0,2,4));

    // Example of adapting noise function from smoke_fragment.glsl
    float noise = sin(fragCoord.x * 0.01 + iTime) * cos(fragCoord.y * 0.01 + iTime);

    // Mixing the color with noise
    col = mix(col, vec3(noise), 0.6);

    // Output to screen
    fragColor = vec4(col,1.0);
}
