float Line1(vec2 p, vec2 a, vec2 b) {
	vec2 pa = p - a, ba = b - a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a+ba*t;
    float d = length(c-p);
    float l = 0.002, g = 0.002;
	return min(1.0, 0.003/d); }

float Line(vec2 p, vec2 a, vec2 b) {
	vec2 pa = p - a, ba = b - a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a+ba*t;
    float d = length(c-p);
    float l = 0.002, g = 0.002;
	return min(1.0, 0.0015/d); }

float Circle(vec2 p, vec2 c) {
    float d = length(p-c);
    return smoothstep(d, 0.0, d - 0.0015); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 uvf = fract(uv*25.0);
    float t = length(0.0 - uv) * 0.94;
    uvf += vec2(0.0, -0.04);
	vec3 col = mix(vec3(0.1, 0.20, 0.45), vec3(0.02, 0.05, 0.1), t);
    col += Line1(uvf, vec2(0.0, 0.0), vec2(1.0, 0.0))*0.2;
    col += Line1(uvf, vec2(0.0, 1.0), vec2(0.0, -1.0))*0.2;
    uv -= vec2(0.1, 0.0);
    col = mix(col, vec3(0.2, 1.0, 0.5), Line(uv, vec2(-0.25, 0.0), vec2(-0.2, 0.0)));
