    col = mix(col, vec3(0.2, 1.0, 0.5), Line(uv, vec2(-0.03, 0.00), vec2(0.00, 0.0)));
    col = mix(col, vec3(0.2, 1.0, 0.5), -Circle(uv, vec2(-0.2,0.0)));
