// linear sRGB component to nonlinear sR'G'B' component
float srgb_gamma(
  float v) {
  return v <= 0.0031308
    ? v * 12.92
    : 1.055 * pow(v, 0.41666666666) - 0.055; }

vec3 srgb_gamma(
  vec3 v) {
  return vec3(
    srgb_gamma(v.r),
    srgb_gamma(v.g),
    srgb_gamma(v.b)); }


void mainImage(out vec4 o, vec2 u) {
  o = texelFetch(iChannel0, ivec2(u), 0);
  o.rgb /= o.a;
  o.rgb = max(vec3(0.0), o.rgb);
  o.rgb = srgb_gamma(o.rgb);
  o.a = 1.0; }

// https://www.shadertoy.com/view/lfs3Wn

// Normal of a plan having a dihedral angle of PI/3 with the YZ plan and PI/5 with the XZ plane
const float CP = cos(3.1415/5.), SP=sqrt(0.75-CP*CP);
const vec3  P35 = vec3(-0.5, -CP, SP);

// Dihedral angles of the Dode. and Ico.
// This probably can be obtained using linera algebra calculations
// https://en.wikipedia.org/wiki/Table_of_polyhedron_dihedral_angles

const float ICODIHEDRAL  = acos(sqrt(5.)/3.);  
const float DODEDIHEDRAL = acos(sqrt(5.)/5.);

// below are the directions from the origin limiting the coordniate's domain after folding space
// trivial, this is the Z axis
const vec3 ICOMIDEDGE = vec3(0,0,1); 
// direction in the XZ plan, the ICO vertex on this line
// I think this is also the normal of a DODE face
const vec3 ICOVERTEX  = normalize(vec3(SP,0.0,0.5)); 
// direction in the YZ plan, you will find the DODE vertex on this line
// I think this is also the normal of an ICO face
const vec3 ICOMIDFACE = normalize(vec3(0.0,SP,CP));  


const float X_TO_ICO_VERTEX  = length(cross(ICOMIDEDGE,ICOVERTEX))/dot(ICOMIDEDGE,ICOVERTEX);
const float Y_TO_DODE_VERTEX = length(cross(ICOMIDEDGE,ICOMIDFACE))/dot(ICOMIDEDGE,ICOMIDFACE);
const float X_TO_DODE_CENTER = X_TO_ICO_VERTEX*cos(DODEDIHEDRAL*.5);
const float Y_TO_ICO_CENTER  = Y_TO_DODE_VERTEX*cos(ICODIHEDRAL*.5);

// https://iquilezles.org/articles/smin
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdSphere( vec3 p, float r )
{
	return length(p) - r;
}

// List of other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and iquilezles.org/articles/distfunctions2d


float sdBlobbyCross( in vec2 pos, float he )
{
    pos = abs(pos);
    pos = vec2(abs(pos.x-pos.y),1.0-pos.x-pos.y)/sqrt(2.0);


    float p = (he-pos.y-0.25/he)/(6.0*he);
    float q = pos.x/(he*he*16.0);
    float h = q*q - p*p*p;

    float x;
    if( h>0.0 ) { float r = sqrt(h); x = pow(q+r,1.0/3.0) - pow(abs(q-r),1.0/3.0)*sign(r-q); }
    else        { float r = sqrt(p); x = 2.0*r*cos(acos(q/(p*r))/3.0); }
    x = min(x,sqrt(2.0)/2.0);

    vec2 z = vec2(x,he*(1.0-2.0*x*x)) - pos;
    return length(z) * sign(z.y);
}

float sdHorseshoe( in vec2 p, in vec2 c, in float r, in vec2 w )
{
    p.x = abs(p.x);
    float l = length(p);
    p = mat2(-c.x, c.y, 
              c.y, c.x)*p;
    p = vec2((p.y>0.0 || p.x>0.0)?p.x:l*sign(-c.x),
             (p.x>0.0)?p.y:l );
    p = vec2(p.x,abs(p.y-r))-w;
    return length(max(p,0.0)) + min(0.0,max(p.x,p.y));
}

float opExtrussion( in vec3 p, in float sdf, in float h )
{
    vec2 w = vec2( sdf, abs(p.z) - h );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float dot2( in vec2 v ) { return dot(v,v); }

float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

float sdSegment( in vec3 p, in vec3 a, in vec3 b )
{
    vec3 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdRoundedX( in vec2 p, in float w, in float r )
{
    p = abs(p);
    return length(p-min(p.x+p.y,w)*0.5) - r;
}

// https://www.shadertoy.com/view/XsXfz2
// distancefield of torus around arbitrary axis z
// similar to https://iquilezles.org/articles/distfunctions
float distTorus(vec3 pos, float r1, float r2, vec3 z)
{
    float pz = dot(pos,normalize(z));
    return length(vec2(length(pos-z*pz)-r1,pz))-r2;
}


// Blackle Mori
vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(p,ax)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

mat2 orient(vec2 a)
{
    return mat2(a.x,a.y,-a.y,a.x);
}


const float PI = 3.14159265359;
const float PHI = (1.+sqrt(5.))/2.;

// folding function - here only 3 fold loops 
// https://www.shadertoy.com/view/ltlGWM
// https://en.wikipedia.org/wiki/Polyhedral_group
vec3 opIcosahedralSymmetry(vec3 p) {    
  // const float c = cos(PI/5.), s=sqrt(0.75-c*c);
  // cos(PI/5.) = 0.80901699437
  // const vec3 n = vec3(-0.5, -c, s);
  const vec3 n = vec3(-0.5, -0.809, 0.309); 
  p = abs(p);
  for (int i = 0; i < 3; i++) {
    float side = dot(p, n);
    if (side > 0.0) break;
    p -= 2.0 * min(0.0, side) * n;
    if (i != 2) {
     p.xy = abs(p.xy); } }
  return p; }    

// some rounded Ico & Dode

float Ico(vec3 p) {
  vec3 q = vec3(p.x - X_TO_ICO_VERTEX, -p.y, p.z - 1.0); // position origin at the vertex
  q.zy *= Rot(ICODIHEDRAL * 0.5); 
  // min added for interior distance
  return length(max(q, 0.0)) + min(q.z, 0.0); }  // z can be used for interior distance

float Dode(vec3 p) {
  vec3 q = vec3(-p.x, p.y - Y_TO_DODE_VERTEX, p.z - 1.0); // position origin at the vertex
  q.zx *= Rot(DODEDIHEDRAL * 0.5); 
  return length(max(q, 0.0)) + min(q.z, 0.0); }

// I played with these functions

float GetDistDodecahedron(vec3 p) {
  vec3 q = p - ICOMIDEDGE;      // move
  q.xz *= Rot(DODEDIHEDRAL * 0.5); // rotate
  // rounded edge
  vec3 vtx = vec3(-q.x, q.y - Y_TO_DODE_VERTEX, q.z);
  // onioning and rounding
  float dDode = abs(length(max(vtx, 0.0)) + min(vtx.z, 0.0) - 0.025) - 0.05; 
  // Holes are centered on the dodecaheron's faces center
  vec2 pCenter = vec2(q.x - X_TO_DODE_CENTER, q.y);
  float dHole = length(pCenter) - 0.3;    
  // Smaller circles are centered somewhere between the middle of the face and the vertex of the Ico
  float dSmallHole = abs(abs(length(q.xy - mix(vec2(0.0, Y_TO_DODE_VERTEX),vec2(X_TO_DODE_CENTER, 0.0), 0.3) ) - 0.07) - 0.030) - 0.015;
  // Spheres on the vertices (dodecahedron's vectrices are icosahedron face's centers)
  float dCorners = length(p - ICOMIDFACE * 1.24) - 0.12;    
  // blobby cross picked from IQ's magic box
  float dBlobbyCross = sdBlobbyCross(vec2(q.x - X_TO_DODE_CENTER * 0.2, q.y) * 13.0, 0.80) / 13.0 - 0.03;
  // combine
  float dist = dDode;
  float dCarvings = min(dHole, min(dSmallHole, dBlobbyCross));
  dist = smax(dist,-(dHole+.1),.04);
  dist += 0.008 * smoothstep(0.01, -0.01, dCarvings);
  dist = smin(dist,dCorners, 0.07);
  return dist * 0.8; }

float GetDistIcosahedron(vec3 p) {
  vec3 q = p - ICOMIDEDGE;     // move
  q.yz *= Rot(ICODIHEDRAL * 0.5); // rotates the coordinates to have XY plan aligned with a ICO face's plan
  // rounded edge
  vec3 vtx = vec3(q.x - X_TO_ICO_VERTEX, -q.y, q.z);
  float dIco = length(max(vtx, 0.0)) + min(vtx.z, 0.0) - 0.10;
  // Circles are centered on the icosahedron's faces
  float dBigHole = abs(abs(abs(length(q.xy - vec2(0.0, Y_TO_ICO_CENTER)) - 0.17) - 0.08) - 0.04) - 0.02;
  // Smaller circles are centered somewhere between the middle of the face and the vertex of the Ico
  float dSmallHole = abs(abs(length(q.xy - mix(vec2(0.0, Y_TO_ICO_CENTER), vec2(X_TO_ICO_VERTEX,0.0), 0.60) ) - 0.07) - 0.025) - 0.010;
  // little chains of spheres on the edges
  float dBalls = length(q - vec3(round(q.x * 20.0) / 20.0, 0.0, 0.1)) - 0.03;
  // Corners of the Ico
  q = p - ICOVERTEX * 1.3;      // move
  q.xz *= Rot(DODEDIHEDRAL * 0.5); // rotate
  float dCorners = length(q) - 0.04 * (1.0 + 0.5 * sin(q.z * 50.0));    
  // combine
  float dist = dIco;
  float dCarvings = min(dBigHole,dSmallHole);
  dist += 0.008 * smoothstep(0.01, -0.01, dCarvings);
  dist = smin(dist,(dCorners - 0.13) * 0.5, 0.1);
  dist = min(dist,dBalls);
  return dist * 0.8; }

// Not sure it is but looks like
float GetDistSmallStellatedDodecahedron(vec3 p) {
  vec3 q = p;
  q -= ICOMIDEDGE;
  float an = DODEDIHEDRAL * 0.5; // angle of a face with the rhombic face
  q.zx *= Rot(an);
  vec2 vtx = q.xy;
  float h = cos(DODEDIHEDRAL * 0.5) * X_TO_ICO_VERTEX; 
  h /= cos(an + DODEDIHEDRAL * 0.5); // Need to calculate again the intersection point with the folding plan
  vtx.x -= h;
  vec2 edge = normalize(vec2(h, -Y_TO_DODE_VERTEX));
  mat2 r = mat2(edge.x, edge.y, -edge.y, edge.x);
  vtx.xy *= r; // align xy coordinates to the edge
  float dStella = length(max(vec3(vtx, q.z), 0.0)) + min(max(vtx.x, max(vtx.y, q.z)), 0.0);  // z can be used for interior distance
  float lines = clamp(q.x - h * 0.55, 0.0, 0.48);
  dStella -= 0.01 * smoothstep(-0.3, 0.9, sin(lines * 3.14 * 20.0));
  float dHorseShoe = sdHorseshoe(q.yx - vec2(0.0, 0.4), vec2(sin(0.45), cos(0.45)), 0.14, vec2(0.04));
  dStella -= 0.1; // rounding
  dStella = smax(dStella, -dHorseShoe, 0.02);
  // get a smooth non-convex dihedral angle is a challenge
  // here I build a base using 2 intersecting planes and smoothed angle
  float dBase = smax(p.z - 1.13, dot(p, ICOMIDFACE) - 1.19, 0.005);
  return smin(dStella,dBase, 0.01) * 0.8; }

// Not sure it is but looks like
float GreatDodecahedron(vec3 p) {
  vec3 q = p;
  q -= ICOMIDEDGE;
  float an = -ICODIHEDRAL;
  q.zy *= Rot(an);
  vec3 vtx = vec3(q.x - X_TO_ICO_VERTEX, -q.y, q.z);
  float dDode = length(max(vtx, 0.0)) + min(vtx.z, 0.0);
  float dist = dDode - 0.05 + 0.008 * smoothstep(0.01, -0.01,sdRoundedX(q.xy - vec2(0, 0.15), 0.1, 0.02));
  return dist * 0.9; }

// Not sure it is but looks like
float GreatStellatedDodecahedron(vec3 p) {
  vec3 q = p;
  q -= ICOMIDEDGE; 
  float an = PI * 0.5 - DODEDIHEDRAL * 0.5; // angle of a face with the rhombic face
  q.zy *= Rot(an);
  // tip of the polyhedron, point of intersection with folding plan
  // TODO : Will make it more understandable using a 2D plan in desmos
  // https://www.desmos.com/calculator/abjapu0oqi
  float h = cos(ICODIHEDRAL * 0.5) * Y_TO_DODE_VERTEX / cos(an + ICODIHEDRAL * 0.5); 
  vec2 vtx = vec2(-q.x,q.y-h); // changing coordinates 
  vec2 edge = normalize(vec2(X_TO_ICO_VERTEX,h));
  vtx.xy *= orient(edge); // align x coordinate to the edge vector
  float dStella = length(max(vec3(vtx, q.z), 0.0)) + min(q.z, 0.0);  // z can be used for interior distance
  float lines = clamp(q.y - h * 0.45, 0.0, 0.48);
  dStella -= 0.01 * smoothstep(-0.3, 0.9,sin(lines * 3.14 * 20.0));
  // get a smooth non-convex dihedral angle is a challenge
  // here I build a base using smoothed intersecting planes
  float dBase = smax(p.z - 1.25, dot(p, ICOVERTEX) - 1.35, 0.04);
  float dist = smin((dStella - 0.1)* 0.9,dBase, 0.02);
  return dist; }

// An experiment using some torus crossing at the intersect of the folding plans
float TorusFlower(vec3 q) {
  float r2 = .025;
  float d = 1e10;  
  {
    float an = PI/3.;   // here you can change the width of the Vesicas
    float r = 1.0;
    vec3 n = vec3(1.,0.,0.);
    n.zx *= Rot(an);   // XZ symetry plane, rotated of an angle around the Y axis
    float dO = dot(ICOMIDFACE,n),         // calculate the distance between the plan and point ICOMIDFACE on the sphere
    rCircle = sqrt(1.-dO*dO);             // radius of the circle at intersection of plan and sphere
    d = distTorus(q-dO*n*r,rCircle*r,r2,n);   // draws the torus based this calculation
  }
  {
    float an = (PI/5.+PI/2.)/2.;   // I found impossible to have a value to make it tangent to both triangle edges
    float r = 0.95;
    vec3 n = erot(P35,normalize(ICOVERTEX-ICOMIDFACE),an); // P35 symetry plan, rotated of an angle around the hypotenuse axis
    float dO = dot(ICOMIDFACE,n),
    rCircle = sqrt(1.-dO*dO);
     d = min(d,distTorus(q-dO*n*r,rCircle*r,r2,n));
  }
  {
    float an = PI/5.;  
    float r = 1.05;
    vec3 n = vec3(0.,1.,0.);
    n.zy *= Rot(an);  
    float dO = dot(ICOVERTEX,n),         
    rCircle = sqrt(1.-dO*dO);            
    d = min(d,distTorus(q-dO*n*r,rCircle*r,r2,n));   
  }
  return d; }

float sdCone(vec3 p, vec2 c) {
  vec2 q = vec2( length(p.xz), -p.y );
  float d = length(q-c*max(dot(q,c), 0.0));
  return d * ((q.x*c.y-q.y*c.x<0.0)?-1.0:1.0); }

// https://www.highlandwoodworking.com/sphereturning.html
// Work in progress
float ChineseBall(vec3 p) {
  vec3 q = p;
  float dDode = length(p)-1.0;
  dDode = abs(dDode-.025)-.05; // onioning and rounding
  // Holes are centered on the dodecaheron's faces center
  q.xz *= Rot(DODEDIHEDRAL*.5); // rotates the coordinates to have XY plan aligned with a DODE face plan
  vec2 pCenter = vec2(q.x - X_TO_DODE_CENTER,q.y);
  pCenter = q.xy;
  float dHole = length( pCenter ) - .55;    
  // shaking mixing the distances
  float dist = dDode;
  dist = smax(dist,-(dHole+.1),.04);
  return dist; }

// IQ & filbs111
float sdOctahedron(vec3 p, float s) {
  p = abs(p);
  float m = p.x + p.y + p.z - s;
  vec3 r = 3.0 * p - m;
  vec3 o = min(r, 0.0);
  o = max(2.0 * r - o * 3.0 + (o.x + o.y + o.z), 0.0);
  return length(p - s * o /(o.x + o.y + o.z)); }

// flockaroo - https://www.shadertoy.com/view/dlcGRH
float GetDistFoldedOctahedron(vec3 p) {
  float dist = sdOctahedron(p, 1.5) - 0.04;
  return dist; }

int shape_count = 8;
int shapeID;
float map(vec3 p) {
  vec3 q = opIcosahedralSymmetry(p);
  if (shapeID == 0) {
    return GetDistDodecahedron(q/1.18)*1.18; }
  else if (shapeID == 1) {
    return GetDistIcosahedron(q/1.15)*1.15; }
  else if (shapeID == 2) {
    return GetDistSmallStellatedDodecahedron(q/0.87)*0.87; }
  else if (shapeID == 3) {
    return GreatDodecahedron(q/1.43)*1.43; }
  else if (shapeID == 4) {
    return GreatStellatedDodecahedron(q/0.55)*0.55; }
  else if (shapeID == 5) {
    return TorusFlower(q/1.4)*1.4; } 
  else if (shapeID == 6) {
    return ChineseBall(q/1.37)*1.37; }
  else if (shapeID == 7) {
    return GetDistFoldedOctahedron(q/0.97)*0.97; } }

// https://www.shadertoy.com/view/lfs3Wn
vec2 triangle_wave(vec2 a){
  vec2 a2 = vec2(1.,0.5), a1 = a + a2;
  return abs(fract(a1 * (a2.x + a2.y)) - 0.5); }

void transform(inout vec2 uv, inout vec2 t2){
  t2 = triangle_wave(uv+.5);
  uv = t2 - triangle_wave(uv.yx) - fract(t2 / 2.0); }

vec2 rotate(vec2 v, float a) {
  float s = sin(a);
  float c = cos(a);
  mat2 m = mat2(c, s, -s, c);
  return m * v; }

vec3 carpet(vec2 uv){
  vec3 col = vec3(0.);
  vec2 t2 = vec2(0.);
  vec3 col1 = col;
  float c1=0.;
  for(int k = 0; k < 15; k++){
    float warp_scale = 16.0;
    vec2 t22 = t2 * warp_scale;
    vec2 warp = vec2(sin(t22.x), cos(t22.y));
    uv.y -= 1./4.;
    float scale = 1.5;
    uv = (uv + t2)/scale;
    uv = (fract(vec2(uv + vec2(0.5,1.5)) * scale) - 0.5) / scale;
    col.x = max(length(uv - t2 - c1) / 3.0, col.x);
    if(k > 1) {
      warp = warp * warp / warp_scale; }
    else {
      warp = vec2(0); }
    vec2 uv_1 = uv + warp.yx, t2_1 = t2 + warp.yx;
    vec3 col_1 = col;
    transform(uv, t2);
    transform(uv_1, t2_1);
    c1 = max(abs(uv_1.y + uv_1.x) / 2.0, c1);
    c1 = max(1.0 - abs(2.0 * c1 - 1.0), c1 / 4.0);
    col.x = max(length(uv_1 - t2_1 - c1) / 3.0, col.x);
    col = abs(col - (1.0 - (c1 * col.x)));
    col1 = abs(col1 * c1 - col - 1.0).yzx; }
  return col1; }

vec3 tex3D(sampler2D tex, vec3 p, vec3 n){
    n = max((abs(n) - 0.2) * 7.0, 0.001);
    n /= (n.x + n.y + n.z); 
	return (
      texture(tex, p.yz) * n.x +
      texture(tex, p.zx) * n.y +
