// integer domain SRGB Color Matching Functions
vec3 srgbcmfi(
  int l) {
  return l >= 360 && l <= 830 ? srgbcmf2[l - 360] : vec3(0.0); }

// nonlinear sR'G'B' component to linear sRGB component
float srgb_igamma(
  float v) {
  return v <= 0.04045
    ? v * 0.0773993808
    : pow((v + 0.055) * 0.94786729857, 2.4) ; }

vec3 srgb_igamma(
  vec3 v) {
  return vec3(
    srgb_igamma(v.r),
    srgb_igamma(v.g),
    srgb_igamma(v.b)); }

//
// math lib
//

// needed to produce infinities
float zero = 0.0;

const float inv_4pi = 0.07957747154;
const float pi = acos(-1.0);

// vector lib

// handy nil vector
const vec4 nil = vec4(0.0);

// basis vectors
const vec4 dx = vec4(1.0, nil.xyz);
const vec4 dy = dx.wxyz;
const vec4 dz = dy.wxyz;
const vec4 dw = dz.wxyz;

vec2 dir2(
  float a) {
  return vec2(cos(a), sin(a)); }

// transformation matrices

mat4 translate(
  vec4 by) {
  return mat4(dx, dy, dz, dw + by); }

mat4 scale(
  float by) {
  return mat4(by * dx, by * dy, by * dz, dw); }

mat4 rotate(
  vec4 axis,
  float angle) {
  axis = normalize(axis);
  float s = sin(angle);
  float c = cos(angle);
  vec4 l = (1.0 - c) * axis;
  vec3 m = l.xyz * axis.yzx;
  vec4 n = s * axis;
  return mat4(
    l.x * axis.x, m.x - n.z, m.z + n.y, 0.0,
    m.x + n.z, l.y * axis.y, m.y - n.x, 0.0,
    m.z - n.y, m.y + n.x, l.z * axis.z, 0.0,
    nil) + scale(c); }

// vector projection
vec3 projectu(
  vec3 v,
  vec3 u) {
  return u * dot(v, u); }

// vector rejection
vec3 rejectu(
  vec3 v,
  vec3 u) {
  return v - projectu(v, u); }

// pseudorandom
// i can't believe it's an LCG
// https://www.ams.org/journals/mcom/1999-68-225/S0025-5718-99-00996-5/S0025-5718-99-00996-5.pdf
uint seed;

void srand(uvec3 coord) {
  uvec3 p = uvec3(37769685u, 26757677u, 20501397u) * coord;
  seed = p.x ^ p.y ^ p.z; }

float unitrand() {
  return pow(2.0, -32.0) * float(seed *= 594156893u); }

float boxrand() {
  return pow(2.0, -32.0) * float(int(seed *= 594156893u)); }

const float eps = 1e-5;

vec3 ro;
vec3 rd;
float t;
vec3 p;
vec3 wl;
vec3 n;
vec3 throughput = vec3(1.0);
vec3 Lo = vec3(0.0);

void texBump(sampler2D tx, float bf){
  const vec2 e = vec2(0.002, 0);
  mat3 m = mat3(
    tex3D(tx, p - e.xyy, n),
    tex3D(tx, p - e.yxy, n),
    tex3D(tx, p - e.yyx, n));
  vec3 gv = vec3(0.299, 0.587, 0.114);
  vec3 g = gv * m;
  g = (g - dot(tex3D(tx, p, n), gv)) / e.x;
  g -= n * dot(n, g);
  n = normalize(n + g * bf);}

float fresnel_reflectance(float ior) {
  float dt1 = dot(rd, -n);
  float ior2 = pow(ior, 2.0);
  float dt12 = pow(dt1, 2.0);
  float discriminant = -1.0 + ior2 + dt12;
  if (discriminant < 0.0) {
    return 1.0; }
  // fresnel reflectance for positive discriminant
  vec3 refracted_heading = normalize(
    rd + (-dt1 + sqrt(discriminant)) * -n);
  float dt2 = dot(refracted_heading, -n);
  float dt22 = pow(dt2, 2.0);
  float k1 = dt12 + ior2 * dt22;
  float k2 = ior2 * dt12 + dt22;
  float v = 2.0 * ior * dt1 * dt2;
  return clamp(0.5 * (
    (k1 - v) / (k1 + v)
    + (k2 - v) / (k2 + v)), 0.0, 1.0); }

vec3 fresnel_reflectance(vec3 ior) {
  return vec3(
    fresnel_reflectance(ior.x),
    fresnel_reflectance(ior.y),
    fresnel_reflectance(ior.z)); }

void cos_icdf() {
  float v = sqrt(unitrand());
  float u = 6.28318530718 * boxrand();
  float r = sqrt(1.0 - pow(v, 2.0));
  vec3 t = normalize(abs(n.y) < 0.5 ? vec3(-n.z, 0.0, n.x) : vec3(-n.y, n.x, 0.0));
  vec3 b = cross(n.xyz, t.xyz);
  rd = vec3(v * n.xyz + r * (cos(u) * t + sin(u) * b)); }

// SRGB Color Matching Functions
vec3 srgbcmf() {
  return
    srgbcmfi(int(wl.x)) * Lo.x +
    srgbcmfi(int(wl.y)) * Lo.y +
    srgbcmfi(int(wl.z)) * Lo.z; }

vec3 quadsorb(
  float c,
  float w) {
  return 1.0 + pow(abs((wl - c) / w), vec3(2.0)); }

float df() {
  return map(p); }

void nf() {
  float d = map(p);
  vec2 e = vec2(eps, 0);
  n = normalize(d - vec3(
    map(p - e.xyy),
    map(p - e.yxy),
    map(p - e.yyx))); }

#define INTERACT 1

void mainImage(out vec4 o, in vec2 xy) {
  srand(uvec3(xy, iFrame));
  shapeID = 
#if INTERACT
  iFrame / (5 * 60) % shape_count;
#else
  0;
#endif
  float wl_min = 360.0;
  float wl_max = 800.0;
  float wl_rng = wl_max - wl_min;
  vec2 wl_pts = wl_min + vec2(1.0, 2.0) * wl_rng / 3.0;
  wl = mix(
    vec3(wl_min, wl_pts.x, wl_pts.y),
    vec3(wl_pts.x, wl_pts.y, wl_max),
    vec3(unitrand(), unitrand(), unitrand()));
  mat4 view =
    rotate(dy, -0.4
#if INTERACT
      - 3.0 * pi * (iMouse.x / iResolution.x)
#endif
      ) *
    rotate(dx, -0.02
#if INTERACT
      + 3.0 * pi * (iMouse.y / iResolution.y)
#endif
    ) *
    translate(vec4(0.0, 0.0, -8.5, 0.0));
  vec2 lenspoint = 0.05 * sqrt(unitrand()) 
    * dir2(6.28318530718 * boxrand());
  ro = vec3(lenspoint, 0.0);
  rd = normalize(vec3(
    (xy - 0.5 * iResolution.xy + vec2(boxrand(), boxrand()))
    / (length(iResolution.xy))
    - (lenspoint / 7.5),
    1.0));
  ro = (view * vec4(ro, 1.0)).xyz;
  rd = (view * vec4(rd, 0.0)).xyz;
  t = 0.0;
  p = ro;
  for (int i = 0; i < 1024; ++i) {
    float dmin = 1.0 / zero;
    float ddode = 1.0 / zero;
    vec3 H = vec3(0.0, 0.0, 0.0) - p;
    float D = dot(H, H) - pow(2.0, 2.0);
    if (D < 0.0 || dot(H, rd) > sqrt(D)) {
      ddode = df();
      dmin = min(ddode, dmin); }
    float dplane = 1.0 / zero;
    if (p.y > -1.5 && rd.y < 0.0) {
      dplane = -(p.y + 1.5) / rd.y;
      dmin = min(dplane, dmin); }
    p = ro + (t + dmin) * rd;
    vec3 ior;
    vec3 albedo;
    vec3 transmittance;
    if (dmin == 1.0 / zero) {
      vec3 room = srgb_igamma(texture(iChannel3, rd).bgr);
      vec3 contrib = room / (1.2 - room);
      Lo += throughput * contrib;
      break; }
    if (dmin == dplane) {
      ro = p; t = 0.0;
      n = dy.xyz;
      albedo = srgb_igamma(0.5 * carpet((ro.xz + vec2(0.0, 10.0)) / 40.0).bgr);
      ior = vec3(1.5);
      transmittance = vec3(1.0); }
    if (dmin == ddode) {
      if (dmin > eps) {
        t += dmin;
        continue; }
      nf();
      if (dot(rd, n) > 0.0) {
        t += eps;
        continue; }
      ro = p; t = 0.0;
      texBump(iChannel1, 0.001);
      albedo = 0.5 / quadsorb(700.0, 100.0);
      ior = 0.5 / quadsorb(400.0, 100.0) + 5.0 / quadsorb(700.0, 150.0);
      transmittance = vec3(0.7); }
    float chance = 0.5;
    vec3 reflectance = 1.0 - transmittance * (1.0 - fresnel_reflectance(ior));
    if (unitrand() < chance) {
      throughput *= reflectance / chance;
      rd = reflect(rd, n); }
    else {
      throughput *= albedo * (1.0 - reflectance) / (1.0 - chance);
      cos_icdf(); } }
  o = vec4(srgbcmf(), 1.0);
  if (true
#if INTERACT
    && iFrame % (5 * 60) != 0
    && iMouse.z <= 0.0 && iMouse.w <= 0.0
#endif
    ) {
    o += texelFetch(iChannel0, ivec2(xy), 0); }
}
