void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec3 col = vec3(sin(500.0*(uv.x-0.5)*(uv.y-0.5))/4.0+0.5);
    if(abs(sin(5.0*uv.x + tan(1.5*iTime)) - 6.0*uv.y+3.0)<0.25)
    {
        col = vec3(sin(uv.x-iTime),cos(uv.x-iTime),tan(uv.x-iTime));
    }
    fragColor = vec4(col,1.0);
}
