#define I iResolution
#define A min( I.x, I.y )
#define rot( a ) mat2( cos(a), sin(a), -sin(a), cos(a) )

void mainImage( out vec4 U, in vec2 V )
{

    vec2 u = ( V + V - I.xy ) / A;

    float p, s = 5., // scale
        r = length(u) - .25, n = max( 
                9., // edge side
            7.
        ), a = 6.28 / n;

    p += smoothstep( 30. / A, 0.,
        fract( r * s )
    ) * iMouse.z / 3e3;

    u *= rot( cos( round( r * s + .3 /* offset */ ) / 3. ) * 3. * iTime
        + atan( u.x, u.y ) * 2. // waves
    );

    u *= rot( round( atan(u.y, u.x) / a ) * a );

    p += ( r * s < 6. ) ? smoothstep( .08, 4. / A, fract( ( max( abs(u.x), abs(u.y) ) - .15 ) * s + .25 ) ) : 0.;

    U = vec4(p);
}
