void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Parameters:
    float multiplier = 0.025;
    float thickness = 0.2;
    float range = 2.0;

    // Mouse input:
    vec2 center = (vec2(iMouse.x/iResolution.x,iMouse.y/iResolution.y)-0.5)*2.0;
    center.x *= iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Keep original coordinates as uv0:
    vec2 uv0 = fragCoord/iResolution.xy;
    // Set center as 0,0
    uv = uv - 0.5;
    uv = uv * 2.0;
    // Ignore aspect ratio:
    uv.x *= iResolution.x / iResolution.y;

    // Looped propagation over time:
    float t = mod(iTime*4., 3.) -1.5 + 0.2 + thickness;

    // Outer and inner mask of effect:
    float mask = 1.0 - smoothstep(t-0.25, t, length(uv-center));
    float imask = smoothstep(t-0.25-thickness, t-thickness, length(uv-center));

    // Finally, displacement:
    vec2 displacement = normalize(uv - center) * multiplier * mask * imask * smoothstep(range, 0., length(uv-center)/range);


    // sample texture and output to screen
    vec4 image = texture(iChannel0, uv0-displacement);
    fragColor = vec4(image.r, image.g, image.b,1.0);

    // Uncomment lines below to see raw effect.
    //float dd = length(displacement) * 10.;
    //fragColor = vec4(dd, dd, dd,1.0);
}
