vec3 hash33(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}


#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 500u

#define pi 3.1415926
#define tau (pi+pi)

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

float sphere(vec3 p, float r)
{
    return length(p) - r;
}

float box(vec3 p, vec3 b) // https://iquilezles.org/articles/distfunctions/
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float cylinder(vec3 p, float r, float h) // based on https://iquilezles.org/articles/distfunctions/
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(r,h);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec4 combine(vec4 a, vec4 b)
{
    return a.w < b.w ? a : b;
}

vec4 intersect(vec4 a, vec4 b)
{
    return a.w > b.w ? a : b;
}

vec4 offset(vec4 c, float d)
{
    return vec4(c.xyz,c.w-d);
}

vec3 brick_material(vec3 p) // https://www.shadertoy.com/view/M3SyzK simple brick material
{
    float thickness = 0.0625;
    float n = 2.0;
    p = n*p*vec3(1,1,4);
    p += vec3(0.5*step(0.5,fract(p.zz/2.)),0);
    if(any(lessThan(fract(p+thickness/2.0),thickness*vec3(1,1,2)))) return vec3(0.5);
    //return vec3(ivec3(floor(p))&1);
    //return hash33(floor(p));
    //return mix(vec3(1,0.2,0.1),vec3(1,0.1,0.2),hash33(floor(p)));
    //return mix(vec3(0.5,0.1,0.05),vec3(0.75,0.05,0.05),hash33(floor(p)));
    return 0.75*mix(vec3(0.5,0.1,0.05),vec3(0.75,0.05,0.05),hash33(floor(p))-hash33(p*123.456));
    //return 0.75*mix(vec3(0.5,0.2,0.1),vec3(0.75,0.1,0.1),hash33(floor(p)))*mix(vec3(0.5),vec3(1.0),hash33(p*123.456));
}

vec4 arch(vec3 p)
{
    if(false) // just for fun...
    {
        p.xy = abs(p.xy);
        if(p.x<p.y) p.xy = p.yx;
    }

    float thickness = 0.02;
    vec3 color = brick_material(p+vec3(0,0.125,0));
    vec3 q = p/vec3(1,4,1)+vec3(0,0.0625,-1);
    q = q.xzy;
    vec3 color2 = brick_material(vec3(24.0*atan(q.y,q.x)/tau,length(q),q.z));
    vec3 color3 = brick_material(vec3(24.0*atan(q.y,q.x)/tau,q.z-0.125,length(q)));
    vec4 combined = vec4(vec3(0),DIST_MAX);
    p.x = abs(p.x);
    combined = combine(combined, vec4(color,box(p-vec3(+2,0,0),vec3(1.0-thickness,0.125-thickness,1))));
    combined = combine(combined, vec4(color,box(p-vec3(0,0,2),vec3(3.0-thickness,0.125-thickness,1))));
    //if(p.z>=1.0&&length(p.xz-vec2(0,1)) <= 1.25) combined.w = cylinder(p, 1.25, 0.25);
    combined = combine(combined, vec4(vec3(color2),max(cylinder((p-vec3(0,0,1)).xzy, 1.25, 0.15),-box(p,vec3(2,1,1)))));
    combined.w = max(combined.w,-cylinder((p-vec3(0,0,1)).xzy, 1.0+thickness, 1.0));
    //combined = combine(combined, vec4(color, box(p-vec3(0,0,-1),vec3(5.0,5.0,0.125-thickness))));
    combined = combine(combined, vec4(brick_material(p), box(p-vec3(0,0,-1),vec3(5.0,5.0,0.125)-thickness)));
    return combined;
}

vec4 map(vec3 p)
{
    return arch(p);
    vec3 color = brick_material(p);
    vec3 color2 = brick_material(vec3(24.0*atan(p.y,p.x)/tau,length(p),p.z));
    vec3 color3 = brick_material(vec3(24.0*atan(p.y,p.x)/tau,p.z-0.125,length(p)));
    vec4 combined = vec4(vec3(0),DIST_MAX);
    combined = combine(combined, vec4(color,box(p-vec3(-1,-1,0),vec3(0.98,0.98,1))));
    combined = combine(combined, vec4(color,cylinder(p-vec3(+1,-1,0),0.98,1.0)));
    combined = combine(combined, vec4(color,sphere(p-vec3(-1,+1,0),0.98)));
    combined = combine(combined, vec4(color,box(p-vec3(+1,+1,0),vec3(0.98,0.98,1))));
    float wall = max(box(p-vec3(0,0,-0.5),vec3(4.98,4.98,0.5)),-box(p,vec3(vec2(4.77),2.0)));
    wall = max(wall, -box(p,vec3(5.0,1.02,2.0)));
    wall = max(wall, -box(p,vec3(1.02,5.0,2.0)));
    combined = combine(combined, vec4(color,wall));
    combined = combine(combined, vec4(color2, max(cylinder(p-vec3(0,0,-0.625),3.5,0.25),-cylinder(p-vec3(0,0,0),3.0,1.0))));
    combined = combine(combined, vec4(color3, max(cylinder(p-vec3(0,0,-0.625-0.02),4.5,0.23),-cylinder(p-vec3(0,0,0),4.0,1.0))));
    combined = combine(combined, vec4(color, box(p-vec3(0,0,-1),vec3(5.0,5.0,0.125))));
    return combined;
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return
        normalize
        (
            k.xyy * map(p + k.xyy*h).w
            +
            k.yyx * map(p + k.yyx*h).w
            +
            k.yxy * map(p + k.yxy*h).w
            +
            k.xxx * map(p + k.xxx*h).w
        );
}

vec4 trace(vec3 ro, vec3 rd, float t0, float t1) // pass on running out of iterations
{
    // NOTE: Limited number of iterations to avoid stalling
    //       when ray passes closely (just above EPSILON)
    //       in parallel to a surface.
    uint i;
    vec4 h = vec4(0);
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        h = map(ro + rd * t);
        if(h.w < EPSILON)
            return vec4(h.xyz,t);
        t += h.w;
    }

    return vec4(h.xyz,t); // stop on running out of iterations
    //return vec4(0,0,0,t1); // pass on running out of iterations
}

// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).w < t1 ? 0.0 : 1.0;
}

// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map(ro + rd*t).w;
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).w;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

void mainImage(out vec4 o, vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (2.0 * I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 4.0/3.0 * pi / 2.0;

    vec3 ro = vec3(0.0, 0.0,-8.0);
    vec3 rd = normalize(vec3(I.xy, 4.)); // NOTE: omitting normalization results in clipped edges artifact

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;
    ro.z += 1.;

    //vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
    //vec3 lightdir = normalize(vec3(1.0,2.0,3.0));
    vec3 lightdir = normalize(vec3(0.0,1.0,1.0));

    vec4 result = trace(ro, rd, 0.0, DIST_MAX);
    vec3 color = result.xyz;
    float dist = result.w;
    if(dist < DIST_MAX)
    {
        vec3 dst = ro + rd * dist;
        vec3 n = normal(dst);

        //color = n * 0.5 + 0.5;

        vec3 ambient = vec3(0.4);
        float brightness = max(dot(lightdir, n), 0.0) * 2.0;
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX, 25.0);
        color *= (ambient * calcAO(dst, n)) + brightness;
/*
        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular;
        }
*/
        vec3 fog_color = vec3(0);
        color = mix(fog_color, vec3(color), exp(-pow(dist/20.0, 2.0))); // fog
    }
    else
    {
        color = vec3(0);
    }

    color = tanh(color); // roll-off overly bright colors
    color = sqrt(color); // approximate gamma

    //color += hash32(I*123.0)/255.0; // dither to remove banding in the background

    o = vec4(color, 1);
}
