#define pi 3.141592653589793

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord / iResolution.xy) * vec2(iResolution.x / iResolution.y, 1.0);

  float d = abs(fract(cos(uv.x*pi*5.)-sin(uv.y*pi*5.)*.2*(1. - cos(iTime))));

  vec3 col = vec3(1. - uv.y - uv.x/4., .5, uv.x*length(uv)/4.);

  fragColor = vec4(vec3(col)*d, 1.0);
}
