vec3 pallet(float t){
    vec3 a = vec3(0.668, 0.338, 0.498);
    vec3 b = vec3(0.858, 0.918, 0.808);
    vec3 c = vec3(1.000, 1.000, 1.000);
    vec3 d = vec3(0.167, 0.277, -1.383);
    return a + b*cos(6.28318*(c*t+d));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;

    uv.x *= iResolution.x/iResolution.y;

    uv *= pow(0.85,iTime);
    uv += vec2(-0.7746806106269039, 0.1374168856037867);

    vec2 z = vec2(0.,0.);
    vec2 c = uv;

    vec3 r = vec3(0.);
    for(int i = 0; i<500; i++)
    {
        z = vec2((z.x*z.x)-(z.y*z.y)+c.x,(2.*z.x*z.y)+c.y);
        if(z.x*z.x+z.y*z.y>4.)
        {
            r = pallet(float(i)*0.01);
            break;
        }
    }


    fragColor = vec4(r,1.0);
}
