// It wasn't derived, it was done the dumb way, by feeling and constantly trying to modify it.
// Idea Source:
// https://www.shadertoy.com/view/M3ByzR


void mainImage( out vec4 O, in vec2 U )
{
    vec2 u = U/iResolution.x;
    u.x-=.5;


    float x,t = iTime, a=1.618;//2.;//1.618;

    //t = log(t)/log(a)*1e2;

  //u *= a*a+mod(t, a);
    u *= pow(a, fract(t)); 

    //x = log2(u.x  );   
    //u /= exp2(  floor (x));

    x = log(u.y)/log(a);
    u /= pow(a,  floor (x));

    O = clamp(vec4(.8)-exp(-12.*fract(x)),0.,1.);


    u -= floor(u)+.5;
    if(length(u+vec2(0,.35))<.4)O--;
}
