#define PIXEL_SIZE 1.0
#define BRIGHTNESS 1.85
//Based off https://www.shadertoy.com/view/4syXD1

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float plx = iResolution.x * PIXEL_SIZE;
    float ply = iResolution.y * PIXEL_SIZE;

    float dx = plx * (0.00625 / iResolution.x);
    float dy = ply * (0.00611 / iResolution.y);

    uv.x = dx * floor(uv.x / dx);
    uv.y = dy * floor(uv.y / dy);

    fragColor = floor(texture(iChannel0, uv) * BRIGHTNESS);
}
