void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    float time = iTime;
    float waterLevel = 0.5 + 0.3 * sin(time); 
    float wave = sin(uv.x * 10.0 + time) * 0.05 + sin(uv.y * 15.0 + time * 1.5) * 0.05;
    vec3 waterColor = vec3(0.5, 0.25, 0.0); 
    float waterOpacity = 0.4; 

    if (uv.y < waterLevel + wave) {

        vec3 backgroundColor = vec3(0);

        vec3 color = mix(backgroundColor, waterColor, waterOpacity);

        fragColor = vec4(color, 1.0);
    } else {
        vec3 backgroundColor = vec3(0.8, 0.8, 0.8); 
        fragColor = vec4(backgroundColor, 1.0);
    }
}
