float nPolygon(vec2 uv, float radius, int sides, float PI) {
    float a = atan(uv.y, uv.x) + PI;
    float r = 2.0 * PI / float(sides);
    //adjust size
    return step(radius, cos(floor(0.5 + a / r) * r - a) * length(uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;



    //parameter
    float radius = 0.3;
    int sides = 5 + int(mod(iTime, 12.0)); 
    float PI = 3.1415;

    float d = 1.0-nPolygon(uv,radius,sides,PI);

    vec3 col = vec3(d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
