// This will be our main fragment shader
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Background color (sky)
    vec3 color = vec3(0.5, 0.8, 1.0);

    // Grass
    float grassHeight = 0.3;
    if (uv.y < grassHeight)
    {
        color = vec3(0.2, 0.8, 0.2);
    }

    // Simple face shape
    // Animation parameters
    float time = iTime;
    float speed = 1.0;
    float lionX = mod(time * speed, 1.0);
    float lionY = 0.2 + 0.1 * sin(time * 2.0);

    // face shape
    vec2 lionPos = vec2(lionX, lionY);
    float lionSize = 0.05;

    if (distance(uv, lionPos) < lionSize)
    {
        color = vec3(1.0, 0.6, 0.1); // Lion color
    }

    // Lion face details
    float eyeSize = 0.01;
    vec2 leftEyePos = lionPos + vec2(-0.02, 0.01);
    vec2 rightEyePos = lionPos + vec2(0.02, 0.01);

    if (distance(uv, leftEyePos) < eyeSize || distance(uv, rightEyePos) < eyeSize)
    {
        color = vec3(0.0); // Eye color
    }

    // Happy mouth
    vec2 mouthPos = lionPos + vec2(0.0, -0.02);
    float mouthSize = 0.03;
    if (distance(uv, mouthPos) < mouthSize && uv.y < lionPos.y - 0.02)
    {
        color = vec3(0.0); // Mouth color
    }

    // Output to screen
    fragColor = vec4(color, 1.0);
}
