// A modified version of GhettoWolf's Brick Vort
// ref:https://www.shadertoy.com/view/Xdl3WH
#define PI 3.1416

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0 * fragCoord.xy / iResolution.xy - 1.0) * vec2(iResolution.x / iResolution.y, 1.0);

    vec2 mouse = (2.0 * iMouse.xy / iResolution.xy - 1.0) * vec2(iResolution.x / iResolution.y, 1.0);

    if (iMouse.z > 0.0) {
        p -= mouse;
        float distance = length(p);
        float angle = atan(p.y, p.x) + distance * 4.0 * PI * (sin(iTime) * 0.5 + 0.5);
        p = distance * vec2(cos(angle), sin(angle)) + mouse;
    }

    vec2 uv = vec2(atan(p.y, p.x) * 1.0 / PI, 1.0 / sqrt(dot(p, p))) * vec2(2.0, 1.0);

    uv.x += sin(2.0 * uv.y + iTime * 0.5);

    vec3 c = texture(iChannel0, uv).xyz;

    c.rg += 0.5 * sin(iTime + uv.xy * PI);
    c.b += 0.5 * sin(iTime + (uv.x + uv.y) * PI);

    c /= (uv.y * 0.5 + 1.0);

    fragColor = vec4(c, 1.0);

}
