#define sizeVariation 1.3
#define stars 1.0

//const float a = 50.; const float b = 10.;

//const float a = 10.; const float b = 10.;

//const float a = 20.; const float b = 10.;

//const float a = 20.; const float b = 20.;

//const float a = 200.; const float b = 20.;

const float a = 16.; const float b = 55.;

float random(float n) {
    return fract(sin(n) * 1e4);
}

float star(vec2 uv, float size) {
    vec2 grid = fract(uv / size);// - 0.25;
    grid.x -= 0.165;
    grid.y -= 0.230;
    float d = length(grid);

    float sizeVar = 0.5 + 0.5 * sin(iTime * 0.3 + uv.x * a + uv.y * b);

    float starShape = smoothstep(0.05 * sizeVar, 0.075 * sizeVar, 0.065 - d);

    return starShape;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord.xy / iResolution.xy) - 0.5;

    vec4 starColor = vec4(0.0);

   float starSize = (iResolution.x*2.0 + random(1.) * iResolution.y*2.0) * sizeVariation;
    vec2 starPos = vec2(random(1. * 54.0), random(1. * 32234.0));

    starColor += vec4(star(fragCoord.xy + starPos * iResolution.xy, starSize));
    fragColor = starColor;
}
