float sdf_rect_annuer(vec2 point, vec2 half_size) {

    point = abs(point);

    // 计算每个点到两个边的距离
    float x_dist = point.x - half_size.x;
    float y_dist = point.y - half_size.y;
  	// 如果一正一负，或者全是负数
  	// 则可以直接取max(x_dist, y_dist)作为对应点的SDF值

    // distance to corner
    float c_dist = length(point - half_size); // 求一下到corner的距离
    // 如果都为正数，说明是C、D点的情况，则要返回到corner的距离
    float res = x_dist > 0.0 && y_dist > 0.0 ?
           c_dist :
           max(x_dist, y_dist);
    return  abs(res) - 0.02 ;
}

float sdf_rect(vec2 point, vec2 half_size) {

    point = abs(point);

    // 计算每个点到两个边的距离
    float x_dist = point.x - half_size.x;
    float y_dist = point.y - half_size.y;
  	// 如果一正一负，或者全是负数
  	// 则可以直接取max(x_dist, y_dist)作为对应点的SDF值

    // distance to corner
    float c_dist = length(point - half_size); // 求一下到corner的距离
    // 如果都为正数，说明是C、D点的情况，则要返回到corner的距离
    float res = x_dist > 0.0 && y_dist > 0.0 ?
           c_dist :
           max(x_dist, y_dist);
    return  res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	float d =  2.4 * sdf_rect(p/0.8, vec2(0.2, 0.4));
    float stepD = step(0.0, d);

    vec3 color1 = vec3(0.7, 0.45, 0.67);
    vec3 color2 = vec3(1., 1., 1.);

    vec3 color = mix(color1, color2, stepD);

	fragColor = vec4(color, 1.0);
}
