void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.5;

    float mountain1 = smoothstep(0.4, 0.45, abs(uv.y - 0.2)) * smoothstep(0.4, 0.45, abs(uv.x - 0.5));
    float mountain2 = smoothstep(0.3, 0.35, abs(uv.y - 0.5)) * smoothstep(0.3, 0.35, abs(uv.x - 0.8));
    float mountain3 = smoothstep(0.2, 0.25, abs(uv.y - 0.8)) * smoothstep(0.2, 0.25, abs(uv.x - 0.2));

    vec3 color = vec3(0.5 + 0.5 * sin(time + mountain1 * 3.0),
                      0.5 + 0.5 * sin(time + mountain2 * 3.0),
                      0.5 + 0.5 * sin(time + mountain3 * 3.0));

    fragColor = vec4(color, 1.0);
}
