/*

Notes:

Very similar to Shard Noise from ENDESGA, and Cubism Noise from Brian Sharpe.
the idea here is we compute the winding number to random triangles at each grid vertex and then weight them with smoothstep weights.
The upside to this method is it relies only on 8 sampling points.
By using winding numbers we also get the benefit of a known value range, in the base case it's always between -1 and 1.
The downside is unlike Shard Noise from ENDESGA this method doesn't smooth out over a wide range since it has a 
smaller vertex neighborhood.

I've also included a "smooth" version, which lets you shrink the shard size to some small value, removing the shard look.

*/






/* CONTROLS - PLAY WITH THESE!!!!!!!  */

#define SMOOTH_NOISE 0

#define ABS_NOISE 0

#define SCROLL_NOISE 1

#define FIT_VALS_BETWEEN_0_AND_1  0

#define CLAMP_NEGATIVE_VALUES 0


//noise settings:
    const float NOISE_FREQUENCY = 2.0;


//shard settings:

    //regular shard size:
    #if !SMOOTH_NOISE
        //0 - 2, lower values will be darker, unless smooth noise is turned on
        const float SHARD_SIZE = 1.5;

    //smooth noise shard size:
    #else       
        //0-1, lower values reduce the "shard"ness.
        const float SHARD_SIZE = .1;

    #endif


    //0 - 1, jitters the shards positions
    const float SHARD_JITTER_STRENGTH = 1.0;



//octaves settings:
    const int NUMBER_OF_OCTAVES =5;
    const float OCTAVE_BIAS = 0.5;


/* END CONTROLS */ 





#define PI 3.1415926535



vec3 hash(vec3 p)
{
    p = vec3(dot(p, vec3(127.1, 311.7, 74.7)), dot(p, vec3(269.5,183.3,246.1)), dot(p, vec3(113.5, 271.9, 124.6)));
    p = fract(sin(p) * 43758.5453123);
    return p;
}

float random(vec3 pos){
    return fract(sin(dot(pos, vec3(64.25375463, 23.27536534, 86.29678483))) * 59482.7542);
}

vec3 rand_dir(vec3 p){
    vec3 r = hash(p);
    return (r - .5) * 2.0;   
}

float interp(float u){
    return 1.0 - smoothstep(0.0, 1.0, abs(u));
}

vec3 interp3(vec3 u){
    return vec3(interp(u.x), interp(u.y), interp(u.z));
}


float winding_noise(vec3 p, float shard_size){
    vec3 noise_p = floor(p);
    vec3 noise_f = fract(p);

    float out_val = 0.0;

    vec3 blend = interp3(noise_f);

    for(int i = 0; i <= 1; i++)
    for(int j = 0; j <= 1; j++)
    for(int k = 0; k <= 1; k++){

        vec3 o = vec3(i,j,k);

        vec3 g = noise_p  + o;


        //shard direction
        vec3 r = rand_dir(g);


        vec3 r2 = rand_dir(r);
        vec3 e0 = cross(r, r2);
        vec3 e1 = cross(r, e0);


        vec3 offset =  rand_dir(r) * random(g) * SHARD_JITTER_STRENGTH * 1.0;

        vec3 v = o - noise_f + offset;

        //make our triangle
        vec3 t0 = r + r2;
        vec3 t1 = r + e0;
        vec3 t2 = r + e1;

        vec3 a = t0 * shard_size + v;
        vec3 b = t1 * shard_size + v;
        vec3 c = t2 * shard_size + v;

        float al = length(a);
        float bl = length(b);
        float cl = length(c);

        float det = determinant(mat3(a,b,c));
        float div = al * bl * cl + dot(a,b) * cl + dot(b,c) * al + dot(c,a) * bl;

        //compute the winding number
        float winding = atan(det,div) / PI;

        float s = winding;

        #if SMOOTH_NOISE
        s = smoothstep(0.0, 1.0, abs(s) / (shard_size * shard_size))  * sign(s);
        #endif


        #if ABS_NOISE
        s = abs(s);
        #endif

        //note that at larger neighborhoods this weighting scheme will need to change :) 
        vec3 wv = abs(o-blend);



        out_val += s * wv.x * wv.y * wv.z;


    }    


    return out_val;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 p = uv.xyx;
    p.z = 0.;


    p -= .5;
    p *= 2.;
    p.x *= iResolution.x / iResolution.y;
    #if SCROLL_NOISE
    p.z +=  iTime * .05;
    #endif


    float bias = OCTAVE_BIAS;
    float freq = NOISE_FREQUENCY;
    int octaves = NUMBER_OF_OCTAVES;


    float out_val = 0.0;
    for(int i = 0; i < octaves; i++){

        out_val += pow(bias, float(i)) * winding_noise(pow(2.0, float(i)) * p * freq, SHARD_SIZE);
        #if CLAMP_NEGATIVE_VALUES
        out_val = max(out_val,0.);
        #endif
    }



    #if FIT_VALS_BETWEEN_0_AND_1
    out_val = (out_val + 1.0) * .5;
    #endif


    vec3 col = vec3(out_val);



    // Output to screen

    fragColor = vec4(col,1.0);
}
